// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";

import { UD60x18, ud, intoUint256 } from "@prb/math/src/UD60x18.sol";

import "./DivisibleNFT.sol";

// Maybe create a Multisig contract then inherit it in the SimulatedDAOTreasury

contract SimulatedDAOTreasury is IERC721Receiver {
    event Deposit(address indexed sender, uint amount, uint balance);
    event SubmitTransaction(
        address indexed owner,
        uint indexed txIndex,
        address indexed to,
        uint value,
        bytes data
    );
    event ConfirmTransaction(address indexed owner, uint indexed txIndex);
    event RevokeConfirmation(address indexed owner, uint indexed txIndex);
    event ExecuteTransaction(address indexed owner, uint indexed txIndex);

    address[] public owners;
    mapping(address => bool) public isOwner;
    uint public numConfirmationsRequired;

    struct Transaction {
        address to;
        uint value;
        bytes data;
        bool executed;
        uint numConfirmations;
    }

    // mapping from tx index => owner => bool
    mapping(uint => mapping(address => bool)) public isConfirmed;

    Transaction[] public transactions;

    // "DAO" and Divisible NFT functionality
    event Deposited(address indexed token, uint256 indexed tokenId, address indexed divisibleNFT);

    struct UnderlyingToken {
        address token;
        uint256 tokenId;
    }

    struct WrappedToken {
        address token;
        uint256 amount;
        UnderlyingToken underlyingToken;
    }

    WrappedToken[] public tokenAddresses; // abi.encode token address + tokenId

    modifier onlyOwner() {
        require(isOwner[msg.sender], "not owner");
        _;
    }

    modifier txExists(uint _txIndex) {
        require(_txIndex < transactions.length, "tx does not exist");
        _;
    }

    modifier notExecuted(uint _txIndex) {
        require(!transactions[_txIndex].executed, "tx already executed");
        _;
    }

    modifier notConfirmed(uint _txIndex) {
        require(!isConfirmed[_txIndex][msg.sender], "tx already confirmed");
        _;
    }

    constructor(address[] memory _owners, uint _numConfirmationsRequired) {
        require(_owners.length > 0, "owners required");
        require(
            _numConfirmationsRequired > 0 &&
                _numConfirmationsRequired <= _owners.length,
            "invalid number of required confirmations"
        );

        for (uint i = 0; i < _owners.length; i++) {
            address owner = _owners[i];

            require(owner != address(0), "invalid owner");
            require(!isOwner[owner], "owner not unique");

            isOwner[owner] = true;
            owners.push(owner);
        }

        numConfirmationsRequired = _numConfirmationsRequired;
    }

    receive() external payable {
        emit Deposit(msg.sender, msg.value, address(this).balance);
    }

    function submitTransaction(
        address _to,
        uint _value,
        bytes memory _data
    ) public onlyOwner {
        uint txIndex = transactions.length;

        transactions.push(
            Transaction({
                to: _to,
                value: _value,
                data: _data,
                executed: false,
                numConfirmations: 0
            })
        );

        emit SubmitTransaction(msg.sender, txIndex, _to, _value, _data);
    }

    function confirmTransaction(
        uint _txIndex
    ) public onlyOwner txExists(_txIndex) notExecuted(_txIndex) notConfirmed(_txIndex) {
        Transaction storage transaction = transactions[_txIndex];
        transaction.numConfirmations += 1;
        isConfirmed[_txIndex][msg.sender] = true;

        emit ConfirmTransaction(msg.sender, _txIndex);
    }

    function executeTransaction(
        uint _txIndex
    ) public onlyOwner txExists(_txIndex) notExecuted(_txIndex) {
        Transaction storage transaction = transactions[_txIndex];

        require(
            transaction.numConfirmations >= numConfirmationsRequired,
            "cannot execute tx"
        );

        transaction.executed = true;

        (bool success, ) = transaction.to.call{value: transaction.value}(
            transaction.data
        );
        require(success, "tx failed");

        emit ExecuteTransaction(msg.sender, _txIndex);
    }

    function revokeConfirmation(
        uint _txIndex
    ) public onlyOwner txExists(_txIndex) notExecuted(_txIndex) {
        Transaction storage transaction = transactions[_txIndex];

        require(isConfirmed[_txIndex][msg.sender], "tx not confirmed");

        transaction.numConfirmations -= 1;
        isConfirmed[_txIndex][msg.sender] = false;

        emit RevokeConfirmation(msg.sender, _txIndex);
    }

    function getOwners() public view returns (address[] memory) {
        return owners;
    }

    function getTransactionCount() public view returns (uint) {
        return transactions.length;
    }

    function getTransaction(
        uint _txIndex
    )
        public
        view
        returns (
            address to,
            uint value,
            bytes memory data,
            bool executed,
            uint numConfirmations
        )
    {
        Transaction storage transaction = transactions[_txIndex];

        return (
            transaction.to,
            transaction.value,
            transaction.data,
            transaction.executed,
            transaction.numConfirmations
        );
    }

    function deposit(address token, uint256 tokenId) onlyOwner public {
        IERC721(token).safeTransferFrom(msg.sender, address(this), tokenId); // Transfer NFT into this contract

        DivisibleNFT DNFT = new DivisibleNFT(); // Create new Divisible NFT

        IERC721(token).approve(address(DNFT), tokenId);

        DNFT.wrap(address(this), 1e18, IERC721Metadata(token).tokenURI(tokenId), token, tokenId); // Wrap NFT

        tokenAddresses.push(WrappedToken(address(DNFT), 1e18, UnderlyingToken(token, tokenId)));

        // Create a Pull-Payment model to claim fractions of NFT or Just Distribute to them directly

        for (uint i = 0; i < owners.length; i++) {
            UD60x18 allocatedAmount = ud(1e18).div(ud((owners.length * 1e18)));
            DNFT.transferFrom(address(this), owners[i], DNFT.tokenOfOwnerByIndex(address(this), 0), intoUint256(allocatedAmount));
        }

        emit Deposited(token, tokenId, address(DNFT));
    }

    function onERC721Received(address, address, uint256, bytes calldata) external pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }
}

contracts/DivisibleNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";

contract DivisibleNFT is ERC721, ERC721URIStorage, ERC721Enumerable, ERC721Burnable, Ownable, IERC721Receiver {
    using Counters for Counters.Counter;

    event Wrapped(address indexed token, uint256 indexed tokenId);
    event Unwrapped(address indexed token, uint256 indexed tokenId);
    event Transferred(address indexed to, uint256 indexed tokenId, uint256 indexed amount);

    Counters.Counter private _tokenIdCounter;

    mapping(uint256 => uint256) private _tokenAmount;

    struct UnderlyingToken {
        address token;
        uint256 tokenId;
        bool isWrapped;
    }

    UnderlyingToken private _underlyingToken;

    constructor() ERC721("DivisibleNFT", "DNFT") {}

    function getUnderlyingToken() public view returns(address, uint256, bool) {
        return (_underlyingToken.token, _underlyingToken.tokenId, _underlyingToken.isWrapped);
    }

    function getTokenAmount(uint256 tokenId) public view returns(uint256 amount) {
        amount = _tokenAmount[tokenId];
    }

    function wrap(address to, uint256 amount, string memory uri, address token, uint256 tokenId) public onlyOwner { // Multisig/DAO can call this to wrap an underlying NFT
        require(_underlyingToken.isWrapped == false, "A token is already wrapped");

        safeMint(to, amount, uri);

        _underlyingToken = UnderlyingToken(token, tokenId, true);

        IERC721(token).safeTransferFrom(_msgSender(), address(this), tokenId);

        emit Wrapped(token, tokenId);
    }

    function unwrap(address to) public onlyOwner { // Multisig/DAO can call this to unwrap the underlying NFT
        (address token, uint256 tokenId, ) = getUnderlyingToken();

        delete _underlyingToken;

        IERC721(token).safeTransferFrom(address(this), to, tokenId);

        emit Unwrapped(token, tokenId);

        // Maybe we could selfdestruct
        // selfdestruct(payable(to));
    }

    function safeMint(address to, uint256 amount, string memory uri) internal {
        _mint(to, amount, uri);
    }

    function _mint(address to, uint256 amount, string memory uri) internal virtual {
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _safeMint(to, tokenId);
        _tokenAmount[tokenId] = amount;
        _setTokenURI(tokenId, uri);
    }

    function _createSplitToken(address to, uint256 tokenId, uint256 amount) internal virtual {
        _mint(to, amount, ERC721URIStorage.tokenURI(tokenId));

        _tokenAmount[tokenId] -= amount;
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId,
        uint256 amount
    ) public virtual {
        require(amount <= _tokenAmount[tokenId], "amount is too large");

        if (amount == _tokenAmount[tokenId]) {
            ERC721.safeTransferFrom(from, to, tokenId);
        } else {
            _createSplitToken(to, tokenId, amount);
        }

        emit Transferred(to, tokenId, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        transferFrom(from, to, tokenId, _tokenAmount[tokenId]);
    }

    // The following functions are overrides required by Solidity.

    function _beforeTokenTransfer(address from, address to, uint256 tokenId, uint256 batchSize)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);

        delete _tokenAmount[tokenId];
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function onERC721Received(address, address, uint256, bytes calldata) external pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }
}

contracts/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    uint public unlockTime;
    address payable public owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        owner.transfer(address(this).balance);
    }
}

